import React from 'react';
import RegisterButton from './RegisterButton';

const MarkerInfo = ({ place, onClose }) => {
  const isRegistered = place.hasOwnProperty('user');
  const style = {
    position: 'absolute',
    left: `${place.pixelX}px`,
    top: `${place.pixelY - 120}px`, // Adjust this value to position above the marker
    transform: 'translateX(-50%)', // Center horizontally
    zIndex: 1000,
  };

  return (
    <div className="marker-info" style={style}>
      <h3>{place.place_name || place.name}</h3>
      {isRegistered ? (
        <>
          <p>Username: {place.user.username}</p>
          <p>Review: {place.review}</p>
        </>
      ) : (
        <>
          <h3>{place.place_name || place.name}</h3>
          <p>{place.address_name}</p>
          <p>{place.phone}</p>
        </>
      )}
      <div className="button-container">
        <button className="close-button" onClick={onClose}>Close</button>
        {!isRegistered && <RegisterButton place={place} />}
      </div>
    </div>
  );
};

export default MarkerInfo;
